package com.survlogic.surveyhelper.activity.staffCompany.fragments.services.controller;

import android.app.Activity;
import android.content.Context;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.support.v7.widget.StaggeredGridLayoutManager;

import com.survlogic.surveyhelper.activity.staffCompany.fragments.news.adapter.CompanyNewsStaggeredAdapter;
import com.survlogic.surveyhelper.activity.staffCompany.fragments.services.adapter.CompanyServicesStaggeredAdapter;

import java.util.ArrayList;

public class CompanyServicesController {
    private static final String TAG = "CompanyServicesController";

    public interface CompanyServicesControllerListener {
        void refreshView();
    }

    private Context mContext;
    private Activity mActivity;

    private CompanyServicesControllerListener mListenerToActivity;

    private RecyclerView recyclerView;

    private ArrayList<String> mImageUrls = new ArrayList<>();
    private ArrayList<String> mNames = new ArrayList<>();
    private ArrayList<String> mWebUrls = new ArrayList<>();

    private static final int NUM_COLUMNS = 2;

    public CompanyServicesController(Context context, CompanyServicesControllerListener listener) {
        this.mContext = context;
        this.mListenerToActivity = listener;

    }

    public void setRecyclerView(RecyclerView recyclerView) {
        this.recyclerView = recyclerView;
        initImageBitmaps();
    }

    private void initImageBitmaps(){
        mImageUrls.add("https://ctex-inc.com/assets/cteimg/timeoff.gif");
        mNames.add("PTO Request");

        mImageUrls.add("data:image/jpeg;base64,");
        mNames.add("Receipt Tracker");

        mImageUrls.add("https://emergencypreparednesspartnerships.com/wp-content/uploads/2016/08/emergency-preparedness-HR.jpg");
        mNames.add("HR Policies");

        mImageUrls.add("http://projectmanagementhacks.com/wp-content/uploads/2016/02/Standard-Operating-Procedure.jpg");
        mNames.add("Standard Operating Procedures");

        initRecyclerView();

    }

    private void initRecyclerView(){
        CompanyServicesStaggeredAdapter staggeredRecyclerViewAdapter =
                new CompanyServicesStaggeredAdapter(mContext, mNames, mImageUrls);

        StaggeredGridLayoutManager staggeredGridLayoutManager = new StaggeredGridLayoutManager(NUM_COLUMNS, LinearLayoutManager.VERTICAL);

        recyclerView.setLayoutManager(staggeredGridLayoutManager);
        recyclerView.setAdapter(staggeredRecyclerViewAdapter);
    }



}
